using FakeXrmEasy.Abstractions.Plugins.Enums;
using Microsoft.Xrm.Sdk;
using Microsoft.Xrm.Sdk.Messages;
using System;
using System.Runtime.CompilerServices;

[assembly: InternalsVisibleTo("FakeXrmEasy.Plugins.Tests, ")]
[assembly: InternalsVisibleTo("FakeXrmEasy.Plugins.Performance, ")]
[assembly: InternalsVisibleTo("DynamicProxyGenAssembly2, ")]

namespace FakeXrmEasy.Plugins.PluginImages
{
    /// <summary>
    /// Defines if PreImage is available for specific requests and stages
    /// 
    /// ---- Message------Stage--------Pre-Image-----Post-Image------
    ///      Create       PRE             No             No
    ///      Update       PRE             Yes            No
    ///      Delete       PRE             Yes            No
    ///      Upsert       PRE             ??             ??
    ///      Create       POST            No             Yes
    ///      Update       POST            Yes            Yes
    ///      Delete       POST            Yes            No
    ///      Upsert       POST            ??             ??
    /// </summary>
    internal class PreImage
    {
        /// <summary>
        /// Determines if a PreImage is available for the given request and plugin stage
        /// </summary>
        /// <param name="organizationRequestType">The request to check the availability for</param>
        /// <returns></returns>
        internal static bool IsAvailableFor(Type organizationRequestType)
        {
            return organizationRequestType == typeof(UpdateRequest)
                   || organizationRequestType == typeof(DeleteRequest);
        }
    }
}
